import React, { useState } from "react";
import { StyleSheet, Text, View, TouchableOpacity } from "react-native";
import {
  StyledText,
  StyledView,
  StyledContainer,
  StyledNotValid,
  StyledValid,
  StyledNormalText,
  StyledButtonText,
  StyledBoldText,
  StyledButtonStyles,
  StyledTextP,
} from "../styles/StyledComp";

function FirtsScreen() {
  const [counter, setCounter] = useState(0);
  const [name, setName] = useState("");

  const increaseCounter = () => {
    if (counter < 1000) {
      setCounter((current) => current + 1);
    }
  };
  const decreaseCounter = () => {
    if (counter > 0) {
      setCounter((current) => current - 1);
    }
  };

  const autoincrement = () => {
    if (counter < 1000) {
      for (var i = 0; i < 1000 - counter; i++) {
        setCounter((current) => current + 1);
      }
    }
  };

  const reset = () => {
    setCounter((current) => (current = 0));
  };

  const visibleText = () => {
    setName("Juan Eduardo Martínez Figueroa");
    setTimeout(() => {
      setName("");
    }, 5000);
  };

  return (
    <StyledView>

      <StyledText>Counter</StyledText>
      <StyledText>{counter}</StyledText>

      <TouchableOpacity onPress={increaseCounter}>
        <StyledButtonStyles>
          <StyledText>
            <StyledButtonText>+</StyledButtonText>
          </StyledText>
        </StyledButtonStyles>
      </TouchableOpacity>

      <TouchableOpacity onPress={decreaseCounter}>
        <StyledButtonStyles>
            <StyledText>
              <StyledButtonText>-</StyledButtonText>
            </StyledText>
        </StyledButtonStyles>
      </TouchableOpacity>

      <TouchableOpacity onPress={autoincrement}>
        <StyledButtonStyles>
            <StyledText>
              <StyledButtonText>auto</StyledButtonText>
            </StyledText>
        </StyledButtonStyles>
      </TouchableOpacity>

      <TouchableOpacity onPress={reset}>
        <StyledButtonStyles>
            <StyledText>
              <StyledButtonText>reset</StyledButtonText>
            </StyledText>
        </StyledButtonStyles>
      </TouchableOpacity>

      {counter > 10 || counter < 0 ? (
        <StyledText style={{ ...styles.normalText, ...styles.notValid }}>
          {"Not in range"}
        </StyledText>
      ) : (
        <Text
          style={{
            ...styles.normalText,
            color: counter >= 8 ? styles.valid.color : styles.notValid.color,
          }}
        >
          {counter >= 8 ? "Is valid" : "Is not valid"}
        </Text>
      )}

    </StyledView>
  );
}
export default FirtsScreen;
