import React,  { useState } from "react";
import AsyncStorage from '@react-native-community/async-storage';
import { TouchableOpacity} from "react-native";

import {
  StyledText,
  StyledView,
  StyledButtonText,
  StyledButtonStyles,
} from "../styles/StyledComp";

let valores = [];
var valor = 0;

function ThirdScreen() {

  const [getValue, setGetValue] = useState('');

  const Item = ({ title }) => (
    <View style={styles.item}>
      <Text style={styles.title}>{title}</Text>
    </View>
  );

  const getValueFunction = () => {
  
    AsyncStorage.getItem('articuloDelaLista').then(
      (value) =>
        setGetValue(value)
    );
    valores = [{Id:valor,valor:{getValue}}];
    valor=valor+1;
  };

    return (
      <StyledView>

        <StyledText>Lista de articulos del Super Mercado</StyledText>

        <TouchableOpacity onPress={getValueFunction}>
        <StyledButtonStyles>
          <StyledButtonText>
            Refrescar
          </StyledButtonText>
        </StyledButtonStyles>
      </TouchableOpacity>
        
      <StyledText>
      <FlatList
        data={valores}
        renderItem={renderItem}
        keyExtractor={item => item.id}
      />
      </StyledText>

      </StyledView>
    );
  }

  export default ThirdScreen;
