import styled from 'styled-components/native';

export const StyledView = styled.View`
    background-color: white;
    flex: 1;
    justify-content: center;
    align-items: center;
    `;

export const StyledText = styled.Text`
    color: black;
    font-size: 32px;
    `;


// Counter Styles

export const StyledNotValid = styled.Text`
color: red;
`;

export const StyledValid = styled.Text`
color: green;
`;

export const StyledNormalText = styled.Text`
    font-size: 70px;
    color: black;
`;
 
export const StyledButtonText = styled.Text`
font-size: 40px;
color: white;
`;

export const StyledBoldText = styled.Text`
font-weight: bold;
`;

export const StyledButtonStyles = styled.Text`
border-radius: 50px;
padding: 5px;
margin: 10px;
background-color: blue;
width: 150px;
text-align: center;
`;

export const StyledTextP = styled.Text`
font-size: 70px;
`;
