import React from "react";
import {
  StyledText,
  StyledView,
  StyledContainer,
  StyledNotValid,
  StyledValid,
  StyledNormalText,
  StyledButtonText,
  StyledBoldText,
  StyledButtonStyles,
  StyledTextP,
} from "../styles/StyledComp";

function FirtsScreen() {
  return <StyledView style={StyledContainer}></StyledView>;
}
export default FirtsScreen;

const [counter, setCounter] = useState(0);
const [name, setName] = useState("");

const increaseCounter = () => {
  if (counter < 1000) {
    setCounter((current) => current + 1);
  }
};
const decreaseCounter = () => {
  if (counter > 0) {
    setCounter((current) => current - 1);
  }
};

const autoincrement = () => {
  if (counter < 1000) {
    for (var i = 0; i < 1000 - counter; i++) {
      setCounter((current) => current + 1);
    }
  }
};

const reset = () => {
  setCounter((current) => (current = 0));
};

const visibleText = () => {
  setName("Juan Eduardo Martínez Figueroa");
  setTimeout(() => {
    setName("");
  }, 5000);
};
