import React from "react";
import { TouchableHighlight, AppRegistry, TextInput } from "react-native";
import {
  StyledText,
  StyledView,
  StyledNotValid,
  StyledValid,
  StyledNormalText,
  StyledButtonText,
  StyledButtonStyles,
  StyledTextP,
} from "../styles/StyledComp";

function SecondScreen() {
  
  const [articulo, onChangeText] = React.useState("");

  const buttonPressed = () => {

    if({articulo}){
      alert("no campturaste datos");
    }else{
      alert("Producto agregado con exito");
    }

  };

  return (
    <StyledView>
      <StyledNormalText>Articulo</StyledNormalText>
      <TextInput 
      placeholder="ingresa tu articulo"
      value={articulo}
      onChangeText={onChangeText}/>

      <TouchableHighlight onPress={buttonPressed}>
        <StyledButtonStyles>
          <StyledButtonText>
            Agregar
          </StyledButtonText>
        </StyledButtonStyles>
      </TouchableHighlight>
    </StyledView>
  );
}

export default SecondScreen;
