import React,  { useState } from "react";
import AsyncStorage from '@react-native-community/async-storage';
import { TouchableOpacity,FlatList} from "react-native";
import { ListItem } from 'react-native-elements';

import {
  StyledText,
  StyledView,
  StyledButtonText,
  StyledButtonStyles,
} from "../styles/StyledComp";

const valores = [
  {id:'1',title:'huevo'},
  {id:'2',title:'leche'},
  {id:'3',title:'carne'},
];


function ThirdScreen() {

  const [getValue, setGetValue] = useState('');

  const getValueFunction = () => {
  
    AsyncStorage.getItem('articuloDelaLista').then(
      (value) =>
        setGetValue(value)
    );

  };

    return (
      <StyledView>

        <StyledText>Lista de articulos del Super Mercado</StyledText>

        <TouchableOpacity onPress={getValueFunction}>
        <StyledButtonStyles>
          <StyledButtonText>
            Lista
          </StyledButtonText>
        </StyledButtonStyles>
      </TouchableOpacity>
        
      <StyledText>{getValue}</StyledText>

      <StyledText>
        <FlatList 
        data={valores}
        keyExtractor= {item => item.articulo}
        renderItem={({ item }) => {
          return(
            <TouchableOpacity>
              <ListItem chevron articulo={item.articulo}></ListItem>

            </TouchableOpacity>
          )
        }}
        />
      </StyledText>

      </StyledView>
    );
  }

  export default ThirdScreen;
